import { reviewService } from '../services/msg.service'
import { store } from './store.js'
import { ADD_MSG, SET_REVIEWS } from './review.reducer'
import { SET_WATCHED_USER } from './user.reducer'

// Action Creators

export function getActionAddMsg(msg) {
  return { type: ADD_MSG, msg }
}
export function getActionSetWatchedUser(user) {
  return { type: SET_WATCHED_USER, user }
}

export async function loadReviews() {
  try {
    const reviews = await reviewService.query()
    store.dispatch({ type: SET_REVIEWS, reviews })

  } catch (err) {
    console.log('ReviewActions: err in loadReviews', err)
    throw err
  }
}

export async function addMsg(msg) {
  try {
    const addedMsg = await msgService.add(msg)
    store.dispatch(getActionAddMsg(addedMsg))

  } catch (err) {
    console.log('ReviewActions: err in addReview', err)
    throw err
  }
}
