import React, { useState, useEffect, useRef } from 'react'
import { useDispatch, useSelector } from 'react-redux'

import { socketService, SOCKET_EMIT_SEND_MSG, SOCKET_EVENT_ADD_MSG, SOCKET_EMIT_SET_TOPIC } from '../services/socket.service'
import { getActionAddMsg } from '../store/msg.actions'

export function CommentFeed() {
  const [msg, setMsg] = useState({ txt: '', email: '' })
  const [msgs, setMsgs] = useState([])
  const [filteredMsgs, setFilteredMsgs] = useState(msgs)
  const [searchedTxt, setSearchedTxt] = useState('')

  useSelector

  const dispatch = useDispatch()

  useEffect(() => {
    socketService.on(SOCKET_EVENT_ADD_MSG, addMsg)
    return () => {
    socketService.off(SOCKET_EVENT_ADD_MSG, addMsg)
    }
  }, [])

  function addMsg(newMsg) {
    setMsgs(prevMsgs => [...prevMsgs, newMsg])
  }

  function sendMsg(ev) {
    ev.preventDefault()
    const newMsg = { email: msg.email, txt: msg.txt }
    socketService.emit(SOCKET_EMIT_SEND_MSG, newMsg)
    // for now - we add the msg ourself
    addMsg(newMsg)
    dispatch(getActionAddMsg(newMsg))
    setMsg({ txt: '', email: '' })
  }

  function handleFormChange(ev) {
    const { name, value } = ev.target
    setMsg(prevMsg => ({ ...prevMsg, [name]: value }))
  }

  function setFilterBy({ target }) {
    const { value } = target
    setSearchedTxt(value)
    const regex = new RegExp(value, 'i')
    const filteredMsgs = msgs?.filter(msg=> regex.test(msg.txt))
    console.log('filteredMsgs', filteredMsgs)
    setMsgs(filteredMsgs)
  }

  return (
    <section className="chat-app">
      <form onSubmit={sendMsg}>
        <input
          type="text"
          name="email"
          value={msg.email}
          placeholder="Email"
          onChange={handleFormChange} />
        <input
          type="text"
          value={msg.txt}
          onChange={handleFormChange}
          name="txt"
          autoComplete="off"
          placeholder='Message' />
        <button>SUBMIT</button>
      </form>
      <input type="text" value={searchedTxt} placeholder='Filter' onChange={setFilterBy} />
      <ul>
        {msgs.map((msg, idx) => (
          <li key={idx}>
            {/* <img src={`https://www.gravatar.com/avatar/${echo md5(strtolower( trim(msg.email)))}`} style={{width:'30px'}}/> */}
            <img src={`https://www.gravatar.com/avatar/205e460b479e2e5b48aec07710c08d50`} style={{ width: '30px' }} />
            {msg.email}: {msg.txt}
          </li>
        ))}
      </ul>
    </section>
  )
}
