export const SET_MSGS = 'SET_MSGS'
export const ADD_MSG = 'ADD_MSG'

const initialState = {
  reviews: [],
}

export function reviewReducer(state = initialState, action = {}) {
  switch (action.type) {
    case SET_MSGS:
      return { ...state, msgs: action.msgs }
    case ADD_MSG:
      return { ...state, msgs: [...state.msgs, action.review] }
    default:
      return state
  }
}
